  }

  onEntered(node, isAppearing) {
    this.setState({ dimension: null });
    this.props.onEntered(node, isAppearing);
  }

  onExit(node) {
    this.setState({ dimension: this.getDimension(node) });
    this.props.onExit(node);
  }

